import React, { useState, useEffect } from "react";
import Grid from "./components/Grid";

const difficulties = {
  Easy: 16,
  Medium: 50,
  Difficult: 100,
};

function App() {
  const [hoverCount, setHoverCount] = useState(0);
  const [difficulty, setDifficulty] = useState("");
  const [totalSquares, setTotalSquares] = useState(0);
  const [resetKey, setResetKey] = useState(0);
  const [isHoverEnabled, setIsHoverEnabled] = useState(false);
  const [startIndex, setStartIndex] = useState(null);
  const [startTime, setStartTime] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(null);

  const handleHover = () => {
    setHoverCount((prevCount) => prevCount + 1);
  };

  const handleDifficultyChange = (event) => {
    const selectedDifficulty = event.target.value;
    setDifficulty(selectedDifficulty);
    setTotalSquares(difficulties[selectedDifficulty]);
    setHoverCount(0);
    setResetKey((prevKey) => prevKey + 1);
    setIsHoverEnabled(false);
    setStartIndex(Math.floor(Math.random() * difficulties[selectedDifficulty]));
    setStartTime(null);
    setElapsedTime(null);
  };

  const handleStartClick = () => {
    setIsHoverEnabled(true);
    setStartTime(Date.now());
  };

  const handleReset = () => {
    setHoverCount(0);
    setResetKey((prevKey) => prevKey + 1);
    setIsHoverEnabled(false);
    setStartIndex(Math.floor(Math.random() * totalSquares));
    setStartTime(null);
    setElapsedTime(null);
  };

  useEffect(() => {
    if (hoverCount === totalSquares - 1) {
      const endTime = Date.now();
      setElapsedTime((endTime - startTime) / 1000); // Calculate elapsed time in seconds
    }
  }, [hoverCount, totalSquares, startTime]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div className="flex flex-col items-center mt-4">
          <label htmlFor="difficulty" className="mb-2 text-lg">
            Select Difficulty:
          </label>
          <select
            id="difficulty"
            value={difficulty}
            onChange={handleDifficultyChange}
            className="mb-4 p-2 border border-gray-300 rounded"
          >
            <option value="">Select</option>
            <option value="Easy">Easy</option>
            <option value="Medium">Medium</option>
            <option value="Difficult">Difficult</option>
          </select>
          {difficulty && (
            <Grid
              totalSquares={totalSquares}
              handleHover={handleHover}
              isHoverEnabled={isHoverEnabled}
              startIndex={startIndex}
              handleStartClick={handleStartClick}
              resetKey={resetKey}
            />
          )}
          {hoverCount === totalSquares - 1 && (
            <>
              <p className="text-2xl text-green-500 mt-4">Victory!</p>
              {elapsedTime && (
                <p className="text-lg text-white">
                  Time: {elapsedTime.toFixed(2)} seconds
                </p>
              )}
            </>
          )}
          {difficulty && (
            <button
              onClick={handleReset}
              className="mt-4 p-2 bg-blue-500 text-white rounded hover:bg-blue-700"
            >
              Reset
            </button>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
