import React, { useState } from "react";
import Card from "./components/Card";

const difficulties = {
  Easy: 16,
  Medium: 50,
  Difficult: 100,
};

function App() {
  const [hoverCount, setHoverCount] = useState(0);
  const [difficulty, setDifficulty] = useState("Easy");
  const [totalSquares, setTotalSquares] = useState(difficulties[difficulty]);
  const [resetKey, setResetKey] = useState(0); // Add reset key to force re-render

  const handleHover = () => {
    setHoverCount((prevCount) => prevCount + 1);
  };

  const handleDifficultyChange = (event) => {
    const selectedDifficulty = event.target.value;
    setDifficulty(selectedDifficulty);
    setTotalSquares(difficulties[selectedDifficulty]);
    setHoverCount(0); // Reset hover count when changing difficulty
    setResetKey((prevKey) => prevKey + 1); // Force re-render of cards
  };

  const handleReset = () => {
    setHoverCount(0);
    setResetKey((prevKey) => prevKey + 1); // Force re-render of cards
  };

  const renderGrid = () => {
    let grid = [];
    for (let i = 0; i < totalSquares; i++) {
      grid.push(<Card key={`${resetKey}-${i}`} onHover={handleHover} />);
    }
    return grid;
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="flex flex-col items-center mt-4">
          <label htmlFor="difficulty" className="mb-2 text-lg">
            Select Difficulty:
          </label>
          <select
            id="difficulty"
            value={difficulty}
            onChange={handleDifficultyChange}
            className="mb-4 p-2 border border-gray-300 rounded"
          >
            <option value="Easy">Easy</option>
            <option value="Medium">Medium</option>
            <option value="Difficult">Difficult</option>
          </select>
          <div className="grid-container flex flex-wrap justify-center">
            {renderGrid()}
          </div>
          {hoverCount === totalSquares && (
            <p className="text-2xl text-green-500 mt-4">Victory!</p>
          )}
          <button
            onClick={handleReset}
            className="mt-4 p-2 bg-blue-500 text-white rounded hover:bg-blue-700"
          >
            Reset
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;
