// src/App.js
import logo from "./logo.svg";
import "./css/App.css";
import React, { useState } from 'react';
import Card from './components/Card';

function App() {
  const [hoverCount, setHoverCount] = useState(0);
  const totalSquares = 16;

  const handleHover = () => {
    setHoverCount(prevCount => prevCount + 1);
  };

  const renderGrid = () => {
    let grid = [];
    for (let i = 0; i < totalSquares; i++) {
      grid.push(<Card key={i} onHover={handleHover} />);
    }
    return grid;
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div className="grid-container flex flex-wrap justify-center mt-4">
          {renderGrid()}
        </div>
        {hoverCount === totalSquares && (
          <p className="text-2xl text-green-500 mt-4">Victory!</p>
        )}
      </header>
    </div>
  );
}

export default App;
