import React, { useState, useEffect } from 'react';
import Grid from './components/Grid';

const difficulties = {
  Easy: 16,
  Medium: 50,
  Difficult: 100,
};

const bombCounts = {
  Easy: 2,
  Medium: 5,
  Difficult: 10,
};

function App() {
  const [hoverCount, setHoverCount] = useState(0);
  const [difficulty, setDifficulty] = useState("");
  const [totalSquares, setTotalSquares] = useState(0);
  const [resetKey, setResetKey] = useState(0);
  const [isHoverEnabled, setIsHoverEnabled] = useState(false);
  const [startIndex, setStartIndex] = useState(null);
  const [startTime, setStartTime] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(null);
  const [isDefeated, setIsDefeated] = useState(false);
  const [bombIndices, setBombIndices] = useState([]);
  const [gameStarted, setGameStarted] = useState(false);

  const handleHover = () => {
    setHoverCount(prevCount => prevCount + 1);
  };

  const handleBombHover = () => {
    setIsDefeated(true);
    setIsHoverEnabled(false);
    setElapsedTime((Date.now() - startTime) / 1000);
  };

  const generateBombIndices = (difficulty) => {
    const bombCount = bombCounts[difficulty];
    const bombIndices = new Set();
    while (bombIndices.size < bombCount) {
      const randomIndex = Math.floor(Math.random() * difficulties[difficulty]);
      bombIndices.add(randomIndex);
    }
    return Array.from(bombIndices);
  };

  const handleDifficultyChange = (event) => {
    const selectedDifficulty = event.target.value;
    const bombIndices = generateBombIndices(selectedDifficulty);

    setDifficulty(selectedDifficulty);
    setTotalSquares(difficulties[selectedDifficulty]);
    setHoverCount(0);
    setResetKey(prevKey => prevKey + 1);
    setIsHoverEnabled(false);
    setStartIndex(Math.floor(Math.random() * difficulties[selectedDifficulty]));
    setStartTime(null);
    setElapsedTime(null);
    setIsDefeated(false);
    setBombIndices(bombIndices);
    setGameStarted(false);
  };

  const handleStartClick = () => {
    setIsHoverEnabled(true);
    setStartTime(Date.now());
    setGameStarted(true);
  };

  const handleReset = () => {
    const bombIndices = generateBombIndices(difficulty);
    setHoverCount(0);
    setResetKey(prevKey => prevKey + 1);
    setIsHoverEnabled(false);
    setStartIndex(Math.floor(Math.random() * totalSquares));
    setStartTime(null);
    setElapsedTime(null);
    setIsDefeated(false);
    setBombIndices(bombIndices);
    setGameStarted(false);
  };

  useEffect(() => {
    if (hoverCount === totalSquares - bombCounts[difficulty] - 1) {
      setElapsedTime((Date.now() - startTime) / 1000);
    }
  }, [hoverCount, totalSquares, bombCounts, difficulty, startTime]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div className="flex flex-col items-center mt-4">
          <label htmlFor="difficulty" className="mb-2 text-lg">Select Difficulty:</label>
          <select
            id="difficulty"
            value={difficulty}
            onChange={handleDifficultyChange}
            className="mb-4 p-2 border border-gray-300 rounded"
          >
            <option value="">Select</option>
            <option value="Easy">Easy</option>
            <option value="Medium">Medium</option>
            <option value="Difficult">Difficult</option>
          </select>
          {difficulty && (
            <Grid
              totalSquares={totalSquares}
              handleHover={handleHover}
              isHoverEnabled={isHoverEnabled}
              startIndex={startIndex}
              handleStartClick={handleStartClick}
              resetKey={resetKey}
              bombIndices={bombIndices}
              handleBombHover={handleBombHover}
              gameStarted={gameStarted}
            />
          )}
          {hoverCount === totalSquares - bombCounts[difficulty] - 1 && !isDefeated && (
            <>
              <p className="text-2xl text-green-500 mt-4">Victory!</p>
              {elapsedTime && <p className="text-lg text-white">Time: {elapsedTime.toFixed(2)} seconds</p>}
            </>
          )}
          {isDefeated && (
            <>
              <p className="text-2xl text-red-500 mt-4">Defeat!</p>
              {elapsedTime && <p className="text-lg text-white">Time: {elapsedTime.toFixed(2)} seconds</p>}
            </>
          )}
          {difficulty && (
            <button
              onClick={handleReset}
              className="mt-4 p-2 bg-blue-500 text-white rounded hover:bg-blue-700"
            >
              Reset
            </button>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
