// src/components/Card.js
import React, { useState } from "react";

const Card = ({ onHover, isHoverEnabled, isStart, onStartClick }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    if (isHoverEnabled && !isHovered) {
      setIsHovered(true);
      onHover();
    }
  };

  return (
    <div
      className={`card w-24 h-24 border border-gray-300 m-1 transition-colors duration-300 ${
        isHovered ? "bg-blue-500" : ""
      }`}
      onMouseEnter={handleMouseEnter}
      onClick={isStart ? onStartClick : null}
    >
      {isStart && (
        <button className="bg-green-500 text-white p-1 rounded">Start</button>
      )}
    </div>
  );
};

export default Card;
